var link=document.querySelector(".contacts-btn"),popup=document.querySelector(".modal"),overlay=document.querySelector(".modal-overlay"),close=popup.querySelector(".modal-close");link.addEventListener("click",function(a){a.preventDefault();popup.classList.add("modal-show");overlay.classList.add("modal-overlay-show")});close.addEventListener("click",function(a){a.preventDefault();popup.classList.remove("modal-show");overlay.classList.remove("modal-overlay-show")});
